import React, { useState } from "react";
import "./index.css";

export default function App() {
  // Tugas 1–3: Form Input
  const [contact, setContact] = useState({
    firstName: "",
    lastName: "",
    email: ""
  });
  const [displayName, setDisplayName] = useState("");

  // Tugas 4: ToDo List
  const [todoInput, setTodoInput] = useState("");
  const [todos, setTodos] = useState([]);

  // Handle perubahan input form (contact)
  function handleContactChange(event) {
    const { name, value } = event.target;
    setContact((prev) => ({
      ...prev,
      [name]: value
    }));
  }

  // Handle submit form contact
  function handleContactSubmit(event) {
    event.preventDefault(); // Solusi agar form tidak refresh (Tugas 2)
    const fullName = `${contact.firstName} ${contact.lastName}`;
    setDisplayName(fullName);
  }

  // Handle ToDo
  function handleTodoChange(event) {
    setTodoInput(event.target.value);
  }

  function addTodo() {
    if (todoInput.trim() !== "") {
      setTodos((prev) => [...prev, todoInput]);
      setTodoInput("");
    }
  }

  function deleteTodo(index) {
    setTodos((prev) => prev.filter((_, i) => i !== index));
  }

  return (
    
    <div style={{ padding: "20px", fontFamily: "Arial" }}>
      {/* Form Contact */}
      <h2>Form Contact (Tugas 1–3)</h2>
      <form onSubmit={handleContactSubmit}>
        <input
          name="firstName"
          placeholder="First Name"
          onChange={handleContactChange}
        />
        <input
          name="lastName"
          placeholder="Last Name"
          onChange={handleContactChange}
        />
        <input
          name="email"
          placeholder="Email"
          onChange={handleContactChange}
        />
        <button type="submit">Submit</button>
      </form>
      <h3>Hello {displayName}</h3>
      <p>Email: {displayName && contact.email}</p>

      <hr />

      {/* ToDo List */}
      <h2>ToDo List (Tugas 4)</h2>
      <input
        type="text"
        placeholder="Add a to-do"
        value={todoInput}
        onChange={handleTodoChange}
      />
      <button onClick={addTodo}>Add</button>
      <ul>
        {todos.map((item, index) => (
          <li key={index} onClick={() => deleteTodo(index)}>
            {item}
          </li>
        ))}
      </ul>
    </div>
  );
}
